mapper.select:
SELECT A.*, M.nickname AS extra__writer
FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id
WHERE A.id = #{id}

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate= NOW()
WHERE id = #{id};

UPDATE article
SET hitCount = hitCount + 1
WHERE id = #{id}

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id}

mapper.update:
UPDATE article
SET goodReactionPoint = goodReactionPoint + 1
WHERE id = #{relId}

UPDATE article
SET badReactionPoint = badReactionPoint + 1
WHERE id = #{relId}

UPDATE article
SET goodReactionPoint = goodReactionPoint - 1
WHERE id = #{relId}

UPDATE article
SET badReactionPoint = badReactionPoint - 1
WHERE id = #{relId}
